mapper.select:
SELECT a.ID ID,a.FEATURED,a.TITLE,DATE_FORMAT(a.DATE_TIME, '%Y-%m-%d') DATE_TIME,a.AUTHOR,a.INTRODUCE,b.ID TAG_ID,b.CONTENT TAG_CONTENT FROM (SELECT d.ID ID,d.FEATURED,d.TITLE,d.DATE_TIME,d.AUTHOR,d.INTRODUCE FROM post d
AND d.FEATURED = #{featured}
ORDER BY ID DESC LIMIT #{dbIndex},#{dbNumber}) a LEFT JOIN post_tag e ON a.ID = e.POST_ID LEFT JOIN tag b ON e.TAG_ID = b.ID ORDER BY ID DESC

SELECT a.ID ID,a.FEATURED,a.TITLE,DATE_FORMAT(a.DATE_TIME, '%Y-%m-%d') DATE_TIME,a.AUTHOR,a.INTRODUCE,a.CONTENT,b.ID TAG_ID,b.CONTENT TAG_CONTENT FROM (SELECT d.ID ID,d.FEATURED,d.TITLE,d.DATE_TIME,d.AUTHOR,d.INTRODUCE,CASE d.ID WHEN #{id, jdbcType=INTEGER} THEN d.CONTENT ELSE NULL END CONTENT FROM post d
d.ID = #{id, jdbcType=INTEGER} OR d.ID = (SELECT ID FROM post f WHERE f.ID < #{id, jdbcType=INTEGER} ORDER BY f.ID DESC LIMIT 0,1) OR d.ID = (SELECT ID FROM post g WHERE g.ID > #{id, jdbcType=INTEGER} ORDER BY g.ID LIMIT 0,1)
AND d.FEATURED = #{featured, jdbcType=CHAR}
) a LEFT JOIN post_tag e ON a.ID = e.POST_ID LEFT JOIN tag b ON e.TAG_ID = b.ID ORDER BY ID DESC
